import LMapRoute from "../components/LMapRoute";
import equipment from "../data/equipment.json";
import positionHistory from "../data/equipmentPositionHistory.json"; // Importe o histórico de posições

export default {
  name: "EquipmentMap",
  components: {
    LMapRoute: LMapRoute
  },
  data: function data() {
    return {
      selectedName: null,
      // Nome do equipamento selecionado
      setNome: [],
      // Lista de nomes dos equipamentos para o v-select
      nameToIdMap: new Map(),
      // Mapa para associar nome ao id
      selectedEquipmentId: null,
      // ID do equipamento selecionado
      selectedDate: null,
      // Data selecionada
      setDate: [] // Lista de datas disponíveis para o v-select de datas
    };
  },
  mounted: function mounted() {
    // Preencher a lista de nomes e o mapa de associação nome-id
    this.setNome = equipment.map(function (equip) {
      return equip.name;
    });
    this.nameToIdMap = new Map(equipment.map(function (equip) {
      return [equip.name, equip.id];
    }));

    // Preencher a lista de datas com as datas disponíveis no histórico de posições
    this.setDate = Array.from(new Set(positionHistory.flatMap(function (eq) {
      return eq.positions.map(function (pos) {
        var date = new Date(pos.date).toLocaleDateString("pt-BR");
        return date;
      });
    }))).sort(function (a, b) {
      return new Date(a) - new Date(b);
    }); // Ordenar datas

    // Definir o equipamento e a data selecionados inicialmente
    if (this.setNome.length > 0) {
      this.selectedName = this.setNome[0];
      this.selectedEquipmentId = this.nameToIdMap.get(this.selectedName);
    }
    if (this.setDate.length > 0) {
      this.selectedDate = this.setDate[0]; // Define o primeiro valor de setDate como padrão
    }
  },
  watch: {
    selectedName: function selectedName(newName) {
      // Atualizar o ID do equipamento conforme o nome selecionado
      this.selectedEquipmentId = this.nameToIdMap.get(newName);
    }
  },
  methods: {
    onEquipmentChange: function onEquipmentChange() {
      // Função disparada quando o nome do equipamento é selecionado no v-select
      console.log("Selected Equipment Name:", this.selectedName);
      console.log("Selected Equipment ID:", this.selectedEquipmentId);
    },
    onDateChange: function onDateChange() {
      // Função disparada quando a data é selecionada no v-select
      console.log("Selected Date (BR format):", this.selectedDate);
    }
  }
};
